5、day005(OpenCV中像素算术操作)
知识点： OpenCV中像素算术操作
像素算术操作

加add、减subtract、乘multiply、除divide
saturate_cast(value)

C++
#include 
#include 

using namespace cv;
using namespace std;

int main(int artc, char** argv) {
    Mat src1 = imread("../../opencv_tutorial/data/images/LinuxLogo.jpg");
    Mat src2 = imread("../../opencv_tutorial/data/images/WindowsLogo.jpg");
    if (src1.empty() || src2.empty()) {
        printf("could not load image...\n");
        return -1;
    }
    namedWindow("input", WINDOW_AUTOSIZE);
    imshow("input1", src1);
    imshow("input2", src2);
    int height = src1.rows;
    int width = src1.cols;

    int b1 = 0, g1 = 0, r1 = 0;
    int b2 = 0, g2 = 0, r2 = 0;
    int b = 0, g = 0, r = 0;
    Mat result = Mat::zeros(src1.size(), src1.type());
    for (int row = 0; row (row, col)[0];
                g1 = src1.at(row, col)[1];
                r1 = src1.at(row, col)[2];

                b2 = src2.at(row, col)[0];
                g2 = src2.at(row, col)[1];
                r2 = src2.at(row, col)[2];

                result.at(row, col)[0] = saturate_cast(b1 + b2);
                result.at(row, col)[1] = saturate_cast(g1 + g2);
                result.at(row, col)[2] = saturate_cast(r1 + r2);
        }
    }
    imshow("output", result);

    Mat add_result = Mat::zeros(src1.size(), src1.type());
    add(src1, src2, add_result);
    imshow("add_result", add_result);

    Mat sub_result = Mat::zeros(src1.size(), src1.type());
    subtract(src1, src2, sub_result);
    imshow("sub_result", sub_result);

    Mat mul_result = Mat::zeros(src1.size(), src1.type());
    multiply(src1, src2, mul_result);
    imshow("mul_result", mul_result);

    Mat div_result = Mat::zeros(src1.size(), src1.type());
    divide(src1, src2, div_result);
    imshow("div_result", div_result);

    waitKey(0);
    return 0;
}

