    if ret is False:
        break;
    hsv = cv.cvtColor(frame, cv.COLOR_BGR2HSV)
    dst = cv.calcBackProject([hsv],[0],roi_hist,[0,180],1)

    # ，搜索更新roi区域
    track_box = cv.CamShift(dst, track_window, term_crit)
    track_window = track_box[1]
    print(track_box)
    # 绘制窗口CAM
